import numpy as np
import matplotlib.pyplot as plt

# Створюємо область визначення для x1 і x2
x1 = np.linspace(0, 30, 400)
x2_1 = (320 - 12 * x1) / 6
x2_2 = (192 - 12 * x1) / 24
x2_3 = (180 - 12 * x1) / 18

